import React, { useState } from "react";
import "./App.css";

function App() {
  const [tripData, setTripData] = useState({
    from_city: "",
    destination_country: "",
    start_date: "",
    duration_days: "",
    budget: "",
    interests: "",
  });
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setTripData({ ...tripData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => {
      setPlan("Lorem ipsum dolor sit amet, consectetur adipiscing elit...");
      setLoading(false);
    }, 1500);
  };

  return (
    <div className="App">
      <header className="hero-map">
        <div className="hero-content">
          <h1>Plan Your Dream Trip</h1>
          <p>Let us craft your perfect adventure</p>
          <div className="hero-icons">
            <img src="/airplane.png" alt="airplane" />
            <img src="/colosseum.png" alt="colosseum" />
            <img src="/beach.png" alt="beach" />
          </div>
        </div>
      </header>

      <main className="main-content">
        <section className="form-section">
          <form className="form" onSubmit={handleSubmit}>
            <input name="from_city" value={tripData.from_city} onChange={handleChange} placeholder="From City" required />
            <input name="destination_country" value={tripData.destination_country} onChange={handleChange} placeholder="Destination Country" required />
            <input name="start_date" type="date" value={tripData.start_date} onChange={handleChange} placeholder="Start Date" required />
            <input name="duration_days" value={tripData.duration_days} onChange={handleChange} placeholder="Duration (Days)" required />
            <input name="budget" value={tripData.budget} onChange={handleChange} placeholder="Budget ($)" required />
            <input name="interests" value={tripData.interests} onChange={handleChange} placeholder="Interests" required />
            <button type="submit">{loading ? "Planning..." : "Plan My Trip"}</button>
          </form>

          <div className="ad-box">Advertisement</div>
        </section>

        <section className="ad-box">Advertisement</section>

        {plan && (
          <section className="results">
            <h2>Results</h2>
            <p>{plan}</p>
          </section>
        )}
      </main>

      <footer>ðŸš€ Built with ðŸ’™</footer>
    </div>
  );
}

export default App;
