import React, { useState } from "react";
import "./App.css";

function App() {
  const [tripData, setTripData] = useState({
    name: "",
    from_city: "",
    destination_country: "",
    start_date: "",
    duration_days: "",
    budget_inr: "",
    interests: "",
  });

  const [plan, setPlan] = useState(null);

  const handleChange = (e) => {
    setTripData({ ...tripData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form submitted");

    try {
      const response = await fetch("http://localhost:8000/plan_trip", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ...tripData,
          interests: tripData.interests.split(",").map((i) => i.trim()),
        }),
      });

      const data = await response.json();
      console.log("Response:", data);
      setPlan(data.plan);
    } catch (error) {
      console.error("Error fetching plan:", error);
    }
  };

  return (
    <div className="App">
      <h1 className="title">‚úàÔ∏è AI Travel Planner</h1>
      <form onSubmit={handleSubmit} className="form">
        {Object.keys(tripData).map((key) => (
          <input
            key={key}
            type={key.includes("date") ? "date" : "text"}
            name={key}
            value={tripData[key]}
            placeholder={key.replaceAll("_", " ")}
            onChange={handleChange}
            required
          />
        ))}
        <button type="submit">üìç Plan My Trip</button>
      </form>

      {plan && (
        <div className="plan-output">
          <h2>üß≥ Your AI-Generated Trip Plan</h2>

          <div className="section">
            <h3>üõ´ Flights</h3>
            <pre>{plan.flights}</pre>
          </div>

          <div className="section">
            <h3>üè® Hotels</h3>
            <pre>{plan.hotels}</pre>
          </div>

          <div className="section">
            <h3>üóìÔ∏è Itinerary</h3>
            <pre>{plan.itinerary}</pre>
          </div>

          <div className="section">
            <h3>üõÇ Visa Info</h3>
            <pre>{plan.visa}</pre>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
