// App.js
import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [tripData, setTripData] = useState({
    from_city: "",
    destination_city: "",
    start_date: "",
    duration_days: "",
    budget_inr: "",
    interests: "",
  });
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [showNav, setShowNav] = useState(true);

  const capitalizeWords = (str) => {
    if (!str) return "";
    return str
      .toLowerCase()
      .split(" ")
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
      .join(" ");
  };
  // hide navbar when you scroll down
  useEffect(() => {
    const onScroll = () => setShowNav(window.scrollY === 0);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const handleChange = (e) =>
    setTripData({ ...tripData, [e.target.name]: e.target.value });

  const toggleTheme = () => {
    document.body.classList.toggle("dark-mode");
    setDarkMode((d) => !d);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPlan(null);
    try {
      const res = await fetch("http://localhost:8000/plan_trip", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...tripData,
          interests: tripData.interests
            .split(",")
            .map((i) => i.trim())
            .filter(Boolean),
        }),
      });
      if (!res.ok) throw new Error(res.statusText);
      const { plan } = await res.json();
      setPlan(plan);
    } catch {
      alert("Could not generate plan. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // split on each **Label:** to paragraphs
  const makeParagraphs = (text) =>
    text
      .replace(/\*\*(.+?):\*\*/g, "\n\n**$1:**")
      .split(/\n{2,}/)
      .map((p) => p.trim());

  const renderSection = (raw, withStrong = true) =>
    makeParagraphs(raw).map((para, i) => {
      let content = para.replace(/\*\*(.+?):\*\*/g, "<strong>$1:</strong>").replace(/\*/g, "");
      return (
        <p key={i} dangerouslySetInnerHTML={{ __html: content }} />
      );
    });

  return (
    <div className="App">
      <nav className={`navbar ${showNav ? "" : "hidden"}`}> 
        <h1 className="logo">
          <span role="img" aria-label="globe" className="logo-icon">üåç</span>
          LazyTrip.xyz
        </h1>
        <button className="toggle" onClick={toggleTheme}>
          {darkMode ? "üåû" : "üåô"}
        </button>
      </nav>

      <header className="hero">
        <h1>üåè Plan Your Dream Trip</h1>
        <p>Let AI craft your perfect adventure</p>
      </header>

      {!(loading || plan) ? (
        <form className="form" onSubmit={handleSubmit}>
          {Object.entries(tripData).map(([key, val]) => (
            <input
              key={key}
              name={key}
              type={key.includes("date") ? "date" : "text"}
              value={val}
              placeholder={key.replace(/_/g, " ")}
              onChange={handleChange}
              required
            />
          ))}
          <button type="submit" disabled={loading}>
            {loading ? "Planning..." : "üìç Plan My Trip"}
          </button>
        </form>
      ) : (
        <div className="form form-summary">
          <div className="summary-line">
            <span className="summary-icon">‚úàÔ∏è</span>
            <span>From <b>{capitalizeWords(tripData.from_city)}</b> to <b>{capitalizeWords(tripData.destination_city)}</b></span>
          </div>
          <div className="summary-line">
            <span className="summary-icon">üóìÔ∏è</span>
            <span>For <b>{tripData.duration_days}</b> days, starting <b>{tripData.start_date}</b></span>
          </div>
        </div>
      )}

      {loading && <p className="loading">‚è≥ Planning your dream trip...</p>}

      {plan && (
        <div className="plan-output">
          <h2 className="section-title">üõ´ Flights</h2>
          <div className="card">{renderSection(plan.flights)}</div>

          <h2 className="section-title">üè® Hotels</h2>
          <div className="card">{renderSection(plan.hotels)}</div>

          <h2 className="section-title">üóìÔ∏è Itinerary</h2>
          <div className="card">{renderSection(plan.itinerary)}</div>

          <h2 className="section-title">üõÇ Visa Info</h2>
          <div className="card">{renderSection(plan.visa, false)}</div>
        </div>
      )}

      <footer className="footer">
        üöÄ Built with üíô 
      </footer>
    </div>
  );
}

export default App;
