import React, { useState } from 'react';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    destination: '',
    start_date: '',
    end_date: '',
    preferences: ''
  });

  const [plan, setPlan] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPlan('');

    try {
      const response = await fetch('http://localhost:8000/plan_trip', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      const data = await response.json();
      setPlan(data.plan);
    } catch (err) {
      console.error('Error fetching plan:', err);
    }

    setLoading(false);
  };

  const formatSections = (text) => {
    const sections = text.split(/\n\s*\n/); // split by double newlines
    return sections.map((sec, i) => (
      <div className="card" key={i}>
        <h3 className="section-subtitle">
          {sec.split(':')[0].trim()}:
        </h3>
        <p>{sec.split(':').slice(1).join(':').trim()}</p>
      </div>
    ));
  };

  return (
    <div className="App">
      <h1 className="title">‚úàÔ∏è DreamTrip AI</h1>
      <p className="subtitle">Plan your perfect journey in seconds.</p>

      <form className="form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="destination"
          placeholder="Where to?"
          value={formData.destination}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="start_date"
          value={formData.start_date}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="end_date"
          value={formData.end_date}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="preferences"
          placeholder="Any preferences (food, hiking...)"
          value={formData.preferences}
          onChange={handleChange}
        />
        <button type="submit">
          {loading ? 'Planning...' : 'Generate My Itinerary'}
        </button>
      </form>

      <div className="plan-output">
        {plan && (
          <div className="section">
            <h2 className="section-title">üó∫Ô∏è Your Custom Trip Plan</h2>
            {formatSections(plan)}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
