import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [tripData, setTripData] = useState({
    from_city: "",
    destination_city: "",
    start_date: "",
    duration_days: "",
    budget_inr: "",
    interests: "",
  });
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) =>
    setTripData({ ...tripData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPlan(null);
    try {
      const res = await fetch("http://localhost:8000/plan_trip", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(tripData),
      });
      if (!res.ok) throw new Error(res.statusText);
      const { plan } = await res.json();
      setPlan(plan);
    } catch {
      alert("Could not generate plan. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
<header
  className="hero-map"
  style={{
    background: `url("/map-background.png") center/cover no-repeat`,
  }}
>        <div className="map-overlay" />
        <div className="hero-content">
          <h1>Plan Your Dream Trip</h1>
          <p>Let us craft your perfect adventure</p>
          <div className="hero-icons">
            <img src="/airplane.png" alt="airplane" />
            <img src="/colosseum.png" alt="colosseum" />
            <img src="/beach.png" alt="beach" />
          </div>
        </div>
      </header>

      <form className="form" onSubmit={handleSubmit}>
        {Object.entries(tripData).map(([key, val]) => (
          <input
            key={key}
            name={key}
            type={key.includes("date") ? "date" : "text"}
            value={val}
            placeholder={key.replace(/_/g, " ")}
            onChange={handleChange}
            required
          />
        ))}
        <button type="submit" disabled={loading}>
          {loading ? "Planning..." : "Plan My Trip"}
        </button>
      </form>

      <div className="ad-container">
        <p>Advertisement</p>
      </div>

      {plan && (
        <div className="results">
          <h2>Results</h2>
          <div className="card">
            <p>{plan.itinerary}</p>
          </div>
        </div>
      )}

      <footer className="footer">
        ðŸš€ Built with ðŸ’™
      </footer>
    </div>
  );
}

export default App;
