import React, { useState, useEffect } from "react";
import Lottie from "lottie-react";
import particlesAnimation from "./animations/particles.json";
import travelAnimation from "./animations/travel.json";
import { FaPlaneDeparture, FaHotel, FaCalendarAlt, FaPassport, FaMoon, FaSun } from "react-icons/fa";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import { useCallback } from "react";
import "./App.css";

function App() {
  const [tripData, setTripData] = useState({
    name: "",
    from_city: "",
    destination_country: "",
    start_date: "",
    duration_days: "",
    budget_inr: "",
    interests: "",
  });

  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  const handleChange = (e) => {
    setTripData({ ...tripData, [e.target.name]: e.target.value });
  };

  const toggleTheme = () => {
    setDarkMode(!darkMode);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPlan(null);

    try {
      const response = await fetch("http://localhost:8000/plan_trip", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ...tripData,
          interests: tripData.interests.split(",").map((i) => i.trim()),
        }),
      });

      const data = await response.json();
      setPlan(data.plan);
    } catch (error) {
      console.error("Error fetching plan:", error);
    } finally {
      setLoading(false);
    }
  };

  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine);
  }, []);

  return (
    <div className={`App ${darkMode ? "dark" : ""}`}>
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          fullScreen: { enable: true },
          background: { color: darkMode ? "#1f1f1f" : "#f0f0f0" },
          fpsLimit: 60,
          interactivity: {
            events: {
              onClick: { enable: true, mode: "push" },
              onHover: { enable: true, mode: "repulse" },
              resize: true,
            },
            modes: {
              push: { quantity: 4 },
              repulse: { distance: 100, duration: 0.4 },
            },
          },
          particles: {
            color: { value: darkMode ? "#ffffff" : "#000000" },
            links: {
              color: darkMode ? "#ffffff" : "#000000",
              distance: 150,
              enable: true,
              opacity: 0.5,
              width: 1,
            },
            collisions: { enable: true },
            move: {
              direction: "none",
              enable: true,
              outModes: { default: "bounce" },
              random: false,
              speed: 2,
              straight: false,
            },
            number: { density: { enable: true, area: 800 }, value: 50 },
            opacity: { value: 0.5 },
            shape: { type: "circle" },
            size: { value: { min: 1, max: 5 } },
          },
          detectRetina: true,
        }}
      />

      <nav className="navbar">
        <h1 className="logo">🌐 AI Travel Architect</h1>
        <button className="toggle" onClick={toggleTheme}>
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>
      </nav>

      <header className="hero">
        <div className="overlay"></div>
        <div className="hero-content">
          <Lottie animationData={travelAnimation} loop={true} className="lottie" />
          <h1>Embark on a Dream Journey</h1>
          <p>Your adventure, intelligently designed by AI</p>
        </div>
      </header>

      <form onSubmit={handleSubmit} className="form">
        {Object.keys(tripData).map((key) => (
          <input
            key={key}
            type={key.includes("date") ? "date" : "text"}
            name={key}
            value={tripData[key]}
            placeholder={key.replaceAll("_", " ")}
            onChange={handleChange}
            required
          />
        ))}
        <button type="submit" disabled={loading} className="submit-button">
          {loading ? "Planning..." : "✨ Plan My Trip"}
        </button>
      </form>

      {loading && <p className="loading">⏳ Hold tight! Dream loading...</p>}

      {plan && (
        <div className="plan-output">
          <h2 className="section-title">🧭 Your AI-Powered Trip Plan</h2>

          <div className="section">
            <h3><FaPlaneDeparture /> Flights</h3>
            <div className="card">{plan.flights}</div>
          </div>

          <div className="section">
            <h3><FaHotel /> Hotels</h3>
            <div className="card">{plan.hotels}</div>
          </div>

          <div className="section">
            <h3><FaCalendarAlt /> Itinerary</h3>
            <div className="card">{plan.itinerary}</div>
          </div>

          <div className="section">
            <h3><FaPassport /> Visa Info</h3>
            <div className="card">{plan.visa}</div>
          </div>
        </div>
      )}

      <footer className="footer">
        <p>🌍 Designed by your virtual co-pilot ✈️</p>
      </footer>
    </div>
  );
}

export default App;
