import React, { useState } from "react";
import "./App.css";

const RenderContent = ({ content }) => {
  if (!content) return null;

  const lines = content.split("\n");
  const elements = [];
  let listItems = [];

  const flushList = () => {
    if (listItems.length > 0) {
      elements.push(
        <ul key={`ul-${elements.length}`}>
          {listItems.map((item, index) => (
            <li key={index} dangerouslySetInnerHTML={{ __html: item }} />
          ))}
        </ul>
      );
      listItems = [];
    }
  };

  const processLineContent = (line) =>
    line
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>")
      .replace(/<b>(.*?)<\/b>/gi, "<strong>$1</strong>")
      .replace(/\*/g, "");

  lines.forEach((line, i) => {
    const trimmedLine = line.trim();
    if (!trimmedLine) {
      flushList();
      return;
    }

    if (trimmedLine.startsWith("* ") || trimmedLine.startsWith("- ")) {
      listItems.push(processLineContent(trimmedLine.substring(2).trim()));
    } else {
      flushList();
      if (trimmedLine.startsWith("### ")) {
        elements.push(<h4 key={i}>{processLineContent(trimmedLine.substring(4))}</h4>);
      } else {
        elements.push(<p key={i} dangerouslySetInnerHTML={{ __html: processLineContent(trimmedLine) }} />);
      }
    }
  });

  flushList();
  return elements;
};

function App() {
  const [tripData, setTripData] = useState({
    from_city: "",
    destination_city: "",
    start_date: "",
    duration_days: "",
    budget_inr: "",
    interests: "",
  });
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setTripData({ ...tripData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPlan(null);
    try {
      const res = await fetch("http://localhost:8000/plan_trip/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...tripData,
          interests: tripData.interests.split(",").map((s) => s.trim()),
        }),
      });
      if (!res.ok) throw new Error("Something went wrong");
      const data = await res.json();
      setPlan(data.plan);
    } catch {
      setPlan("Failed to generate trip plan. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      {/* NAVBAR */}
      <nav className="navbar">
        <h1 className="logo">LazyTrip</h1>
      </nav>

      {/* HERO */}
      <header className="hero-map">
        <div className="hero-container">
          <div className="hero-left">
            <h1>Plan Your Dream Trip</h1>
            <p>Let us craft your perfect adventure</p>

            <form className="hero-form" onSubmit={handleSubmit}>
              <input name="from_city" value={tripData.from_city} onChange={handleChange} placeholder="From City" required />
              <input name="destination_city" value={tripData.destination_city} onChange={handleChange} placeholder="Destination City" required />
              <input name="start_date" type="date" value={tripData.start_date} onChange={handleChange} required />
              <input name="duration_days" value={tripData.duration_days} onChange={handleChange} placeholder="Duration (Days)" required />
              <input name="budget_inr" value={tripData.budget_inr} onChange={handleChange} placeholder="Budget (INR)" required />
              <input name="interests" value={tripData.interests} onChange={handleChange} placeholder="Interests (comma-separated)" required />
              <button type="submit">{loading ? "Planning..." : "Plan My Trip"}</button>
            </form>
          </div>

          <div className="hero-right">
            <img src="/airplane.png" alt="airplane" />
            <img src="/colosseum.png" alt="colosseum" />
            <img src="/beach.png" alt="beach" />
          </div>
        </div>
      </header>

      {/* ADS & RESULTS */}
      <main>
        <section className="ad-container">Advertisement</section>

        {plan && (
          <section className="results">
            <h2>Results</h2>
            {typeof plan === "string" ? (
              <p className="error-message">{plan}</p>
            ) : (
              <div className="plan-details">
                <div className="plan-section">
                  <h3>✈️ Flights</h3>
                  <RenderContent content={plan.flights} />
                </div>
                <div className="plan-section">
                  <h3>🏨 Hotels</h3>
                  <RenderContent content={plan.hotels} />
                </div>
                <div className="plan-section">
                  <h3>🗺️ Itinerary</h3>
                  <RenderContent content={plan.itinerary} />
                </div>
                {plan.visa && (
                  <div className="plan-section">
                    <h3>🛂 Visa</h3>
                    <RenderContent content={plan.visa} />
                  </div>
                )}
              </div>
            )}
          </section>
        )}

        <section className="ad-container">Advertisement</section>
      </main>

      {/* FOOTER */}
      <footer>🚀 Built with 💙</footer>
    </div>
  );
}

export default App;
