import React, { useState } from 'react';
import './App.css';
import logo from './logo.svg'; // Add a logo image to src folder

function App() {
  const [formData, setFormData] = useState({
    destination: '',
    start_date: '',
    end_date: '',
    interests: '',
    budget: ''
  });
  const [plan, setPlan] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setPlan('Planning your trip... ‚ú®');

    try {
      const response = await fetch('http://localhost:8000/plan_trip', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setPlan(data.plan);
    } catch (error) {
      console.error('Error fetching plan:', error);
      setPlan('Oops! Something went wrong. Please try again.');
    }
  };

  return (
    <div className="App">
      <header className="hero-header">
        <img src={logo} alt="Logo" className="logo" />
        <h1 className="title">WanderMind</h1>
        <p className="subtitle">Your AI travel dreamweaver ‚úàÔ∏èüåç</p>
      </header>

      <form onSubmit={handleSubmit} className="form">
        <input name="destination" placeholder="Destination" onChange={handleChange} />
        <input type="date" name="start_date" onChange={handleChange} />
        <input type="date" name="end_date" onChange={handleChange} />
        <input name="interests" placeholder="Interests (e.g., temples, anime, food)" onChange={handleChange} />
        <input name="budget" placeholder="Budget (INR/USD)" onChange={handleChange} />
        <button type="submit">Get My Dream Plan üåü</button>
      </form>

      <div className="plan-output">
        {plan && (
          <div className="section">
            <h2 className="section-title">‚ú® Your Travel Blueprint</h2>
            <div className="card">{plan}</div>
          </div>
        )}
      </div>

      <footer className="footer">
        <p>Crafted with ‚ù§Ô∏è by WanderMind AI</p>
      </footer>
    </div>
  );
}

export default App;
