import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [tripData, setTripData] = useState({
    name: "",
    from_city: "",
    destination_country: "",
    start_date: "",
    duration_days: "",
    budget_inr: "",
    interests: "",
  });
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  // show/hide navbar on scroll
  const [showNav, setShowNav] = useState(true);
  useEffect(() => {
    const onScroll = () => setShowNav(window.scrollY === 0);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const handleChange = (e) =>
    setTripData({ ...tripData, [e.target.name]: e.target.value });

  const toggleTheme = () => {
    document.body.classList.toggle("dark-mode");
    setDarkMode((d) => !d);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPlan(null);
    try {
      const res = await fetch("http://localhost:8000/plan_trip", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...tripData,
          interests: tripData.interests
            .split(",")
            .map((i) => i.trim())
            .filter(Boolean),
        }),
      });
      if (!res.ok) throw new Error(res.statusText);
      const { plan } = await res.json();
      setPlan(plan);
    } catch {
      alert("Could not generate plan. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // **FORMAT ITINERARY** into an array of “Day X…” paragraphs
  const itineraryParagraphs = plan
    ? plan.itinerary
        // Ensure we start each Day marker on its own line
        .replace(/\*\*Day/g, "\n\n**Day")
        // Split on blank lines into paragraphs
        .split(/\n{2,}/)
        .map((p) => p.trim())
    : [];

  return (
    <div className="App">
      <nav className={`navbar ${showNav ? "" : "hidden"}`}>
        <h1 className="logo">✈️ AI Trip Planner</h1>
        <button className="toggle" onClick={toggleTheme}>
          {darkMode ? "🌞" : "🌙"}
        </button>
      </nav>

      <header className="hero">
        <h1>🌏 Plan Your Dream Trip</h1>
        <p>Let AI craft your perfect adventure</p>
      </header>

      <form className="form" onSubmit={handleSubmit}>
        {Object.entries(tripData).map(([key, val]) => (
          <input
            key={key}
            name={key}
            type={key.includes("date") ? "date" : "text"}
            value={val}
            placeholder={key.replace(/_/g, " ")}
            onChange={handleChange}
            required
          />
        ))}
        <button type="submit" disabled={loading}>
          {loading ? "Planning..." : "📍 Plan My Trip"}
        </button>
      </form>

      {loading && <p className="loading">⏳ Planning your dream trip...</p>}

      {plan && (
        <div className="plan-output">
          <h2 className="section-title">🧳 Your AI Trip Plan</h2>

          <div className="section">
            <h3>🛫 Flights</h3>
            <div className="card">{plan.flights}</div>
          </div>

          <div className="section">
            <h3>🏨 Hotels</h3>
            <div className="card">{plan.hotels}</div>
          </div>

          <div className="section">
            <h3>🗓️ Itinerary</h3>
            <div className="card">
              {itineraryParagraphs.map((para, i) => (
                <p key={i}>{para}</p>
              ))}
            </div>
          </div>

          <div className="section">
            <h3>🛂 Visa Info</h3>
            <div className="card">{plan.visa}</div>
          </div>
        </div>
      )}

      <footer className="footer">
        🚀 Built with 💙 by your AI Travel Agent
      </footer>
    </div>
  );
}

export default App;
