// App.js
import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [tripData, setTripData] = useState({
    from_city: "",
    destination_city: "",
    start_date: "",
    duration_days: "",
    budget_inr: "",
    interests: "",
  });
  const [plan, setPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [showNav, setShowNav] = useState(true);

  useEffect(() => {
    const onScroll = () => setShowNav(window.scrollY === 0);
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const handleChange = (e) =>
    setTripData({ ...tripData, [e.target.name]: e.target.value });

  const toggleTheme = () => {
    document.body.classList.toggle("dark-mode");
    setDarkMode((d) => !d);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setPlan(null);
    try {
      const res = await fetch("http://localhost:8000/plan_trip", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...tripData,
          interests: tripData.interests
            .split(",")
            .map((i) => i.trim())
            .filter(Boolean),
        }),
      });
      if (!res.ok) throw new Error(res.statusText);
      const { plan } = await res.json();
      setPlan(plan);
    } catch {
      alert("Could not generate plan. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const makeParagraphs = (text) =>
    text
      .replace(/\*\*(.+?):\*\*/g, "\n\n**$1:**")
      .split(/\n{2,}/)
      .map((p) => p.trim());

  const renderSection = (raw) =>
    makeParagraphs(raw).map((para, i) => (
      <p key={i}>{para.replace(/\*\*(.+?):\*\*/, "$1:")}</p>
    ));

  return (
    <div className="App">
      <nav className={`navbar ${showNav ? "" : "hidden"}`}>
        <h1 className="logo">LazyTrip</h1>
        <button className="toggle" onClick={toggleTheme}>
          {darkMode ? "🌞" : "🌙"}
        </button>
      </nav>

      <header className="hero-map">
        <div className="map-overlay" />
        <div className="hero-content">
          <h1>Plan Your Dream Trip</h1>
          <p>Let us craft your perfect adventure</p>
        </div>
      </header>

      <form className="form" onSubmit={handleSubmit}>
        {Object.entries(tripData).map(([key, val]) => (
          <input
            key={key}
            name={key}
            type={key.includes("date") ? "date" : "text"}
            value={val}
            placeholder={key.replace(/_/g, " ")}
            onChange={handleChange}
            required
          />
        ))}
        <button type="submit" disabled={loading}>
          {loading ? "Planning..." : "Plan My Trip"}
        </button>
      </form>

      <div className="ad-container">
        <p>Ad Space</p>
      </div>

      {loading && <p className="loading">⏳ Planning your dream trip...</p>}

      {plan && (
        <div className="results">
          <h2>Results</h2>
          <div className="card">
            {renderSection(plan.itinerary)}
          </div>
        </div>
      )}

      <footer className="footer">🚀 Built with 💙</footer>
    </div>
  );
}

export default App;
